package mcr.model;

public class Monitor {
	
	private int id;
	
	private String name;

	public Monitor() {
		super();
		// TODO Auto-generated constructor stub
	}

	public Monitor(String name) {
		super();
		this.name = name;
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}
	
	

}
